(async () => {
  const clients = variables.ClientCVS;

  if (!clients || clients.length === 0) {
    alert("لا توجد بيانات عملاء.");
    return;
  }

  // تحميل jsPDF و AutoTable
  function loadScript(src) {
    return new Promise((resolve, reject) => {
      const script = document.createElement("script");
      script.src = src;
      script.onload = resolve;
      script.onerror = reject;
      document.head.appendChild(script);
    });
  }

  await loadScript("https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.1/jspdf.umd.min.js");
  await loadScript("https://cdnjs.cloudflare.com/ajax/libs/jspdf-autotable/3.5.25/jspdf.plugin.autotable.min.js");

  const { jsPDF } = window.jspdf;
  const doc = new jsPDF();

  doc.setFontSize(16);
  doc.text("Clients Report", 105, 20, { align: "center" });

  // حذف ID من العناوين
  const headers = ["Client Name", "Phone", "Email", "Status", "Source", "Assigned To", "Notes"];

  // حذف ID من البيانات
  const rows = clients.map(client => [
    client.client_name,
    client.phone,
    client.email,
    client.status,
    client.source,
    client.assigned_to,
    client.notes
  ]);

  doc.autoTable({
    head: [headers],
    body: rows,
    styles: { fontSize: 9 },
    headStyles: { fillColor: [22, 160, 133], halign: "center" },
    bodyStyles: { halign: "left" },
    startY: 30
  });

  doc.save("clients_export.pdf");
})();

