let tasks = collections['e4fd7594-cca4-4887-ad6d-70ff02fd01f4']?.data || [];
if (!Array.isArray(tasks) || tasks.length === 0) {
  alert("لا توجد مهام للتصدير.");
  return;
}

const headers = [
  "عنوان المهمة",
  "حالة المهمة",
  "الأولوية",
  "تاريخ التنفيذ",
  "اسم المسؤول",
  "الملاحظات"
];

const rows = tasks.map(task => {
  const statusName = task.task_status?.source_name || `ID ${task.task_status}`;
  const priorityName = task.task_priority?.source_name || `ID ${task.task_priority}`;
  const userName = task.profiles?.user_name || `ID ${task.user_id}`;

  return [
    task.name || "",
    statusName,
    priorityName,
    task.date || "",
    userName,
    task.notes || ""
  ];
});

const csvContent = [
  headers.join(","),
  ...rows.map(row => row.map(value => `"${value}"`).join(","))
].join("\n");

const BOM = "\uFEFF";
const blob = new Blob([BOM + csvContent], { type: "text/csv;charset=utf-8;" });
const url = URL.createObjectURL(blob);
const link = document.createElement("a");
link.setAttribute("href", url);
link.setAttribute("download", "Tasks_Export.csv");
link.click();

