async function getGmailMessages(accessToken) {
    try {
        // 1️⃣ هات أول 20 رسالة من الـPrimary Inbox
        const listRes = await fetch(
            "https://gmail.googleapis.com/gmail/v1/users/me/messages?labelIds=INBOX&q=category:primary&maxResults=20",
            {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${accessToken}`,
                    "Accept": "application/json"
                }
            }
        );

        const listData = await listRes.json();
        if (!listData.messages) {
            return [];
        }

        // 2️⃣ هات تفاصيل كل رسالة
        const messages = await Promise.all(
            listData.messages.map(async (msg) => {
                const res = await fetch(
                    `https://gmail.googleapis.com/gmail/v1/users/me/messages/${msg.id}?format=full`,
                    {
                        method: "GET",
                        headers: {
                            "Authorization": `Bearer ${accessToken}`,
                            "Accept": "application/json"
                        }
                    }
                );
                const msgData = await res.json();
                return parseMessage(msgData);
            })
        );

        // ✅ نرتب الأحدث الأول
        messages.sort((a, b) => new Date(b.internalDate) - new Date(a.internalDate));

        return messages;

    } catch (err) {
        console.error("Error fetching Gmail messages:", err);
        return [];
    }
}

// 🛠️ Function مساعدة لاستخراج البيانات من الرسالة
function parseMessage(msg) {
    const headers = msg.payload.headers;
    const subject = headers.find(h => h.name === "Subject")?.value || "";
    const fromRaw = headers.find(h => h.name === "From")?.value || "";
    const snippet = msg.snippet || "";

    // ✅ نضبط الـFrom (نفصل الاسم عن الإيميل لو موجود)
    let from_name = fromRaw;
    let from_email = fromRaw;
    const fromMatch = fromRaw.match(/(.*)<(.*)>/);
    if (fromMatch) {
        from_name = fromMatch[1].trim();
        from_email = fromMatch[2].trim();
    }

    // ✅ التاريخ
    let formattedDate = "";
    if (msg.internalDate) {
        const date = new Date(parseInt(msg.internalDate));
        formattedDate = formatDate(date);
    }

    // ✅ فك تشفير body
    let body = "";
    if (msg.payload.body?.data) {
        body = decodeBase64(msg.payload.body.data);
    } else if (msg.payload.parts) {
        const part = msg.payload.parts.find(p => p.mimeType === "text/plain");
        if (part?.body?.data) {
            body = decodeBase64(part.body.data);
        }
    }

    return {
        id: msg.id,
        subject,
        from_name,
        from_email,
        snippet,
        body,
        date: formattedDate,
        internalDate: msg.internalDate
    };
}

// 🛠️ Base64 decode
function decodeBase64(str) {
    return atob(str.replace(/-/g, "+").replace(/_/g, "/"));
}

// 🛠️ Format التاريخ
function formatDate(date) {
    const day = String(date.getDate()).padStart(2, "0");
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const year = date.getFullYear();

    let hours = date.getHours();
    const minutes = String(date.getMinutes()).padStart(2, "0");
    const ampm = hours >= 12 ? "PM" : "AM";
    hours = hours % 12 || 12;

    return `${day}/${month}/${year} - ${hours}:${minutes} ${ampm}`;
}

