let sources = collections[/* lead_sources */ '166b6c26-2d1d-47bc-9407-08f8b10e76c5']?.data || [];
let users = collections[/* users */ '0feb2a3f-333a-4832-98d1-6da1ec1642b8']?.data || [];

if (!Array.isArray(data) || data.length === 0) {
  console.log("No data to export");
  return;
