const headers = [
  "الاسم",
  "رقم التليفون",
  "البريد الإلكتروني",
  "الحالة",
  "ملاحظات",
  "تاريخ الإضافة",
  "المصدر",
  "المسؤول"
];

const rows = data.map(item => {
  const sourceObj = sources.find(src => src.id === item.source);
  const userObj = users.find(user => user.id === item.assigned_to);

  const sourceName = sourceObj?.source_name || `ID ${item.source}`;
  const userName = userObj?.user_name || `ID ${item.assigned_to}`;

