    item.phone || "",
    item.email || "",
    item.status || "",
    item.notes || "",
    item.created_at || "",
    sourceName,
    userName
  ];
});

const csvContent = [
  headers.join(","),
  ...rows.map(row => row.map(value => `"${value}"`).join(","))
].join("\n");

